<?php
    function active($page, $user) {
        if(isset($_GET['page'])) {
            if ($page == $_GET['page']) {
                echo "active";
            }
        } else {
            switch($user['type']) {
                case "admin":
                case "employee":
                    if ($page == "dashboard") {
                        echo "active";
                    }
                    break;
                case "supplier":
                    if ($page == "chat") {
                        echo "active";
                    }
                    break;
            }
        }
    }

